public function get(string $key) {
	$files = glob(DIR_CACHE . 'cache.' . preg_replace('/[^A-Z0-9\._-]/i', '', $key) . '.*');

	if ($files) {
		return json_decode(file_get_contents($files[0]), true);
	} else {
		return [];
	}
}
